Pyramid=class('Pyramid', Sandbox)function Pyramid:initialize()	Sandbox.initialize(self)		local count=20	local size=1		local groundBody=love.physics.newBody(self.world, 0, 0, "static")	local groundShape=love.physics.newEdgeShape(-20, 0, 20, 0)	local groundFixture=love.physics.newFixture(groundBody, groundShape, 0)				local x={-7, 0.75}	local y={}	local deltaX={0.5625, 1.25}	local deltaY={1.125, 0}		for i=1, count do		y[1]=x[1]		y[2]=x[2]		for j=i, count do			local b=love.physics.newBody(self.world, y[1], y[2], "dynamic")			local s=love.physics.newRectangleShape(size, size)			love.physics.newFixture(b, s, 5)						y[1]=y[1]+deltaY[1]			y[2]=y[2]+deltaY[2]		end				x[1]=x[1]+deltaX[1]		x[2]=x[2]+deltaX[2]	endend